function actionlog(action, state){
    console.groupCollapsed(`%cA%c ${action.type}`, '{color:"#4A0"}', '{color:"#999"}');
    console.log(state);
    console.log(action);
}

export function photosHasError(state = false, action) {
    actionlog(action, state);
    switch (action.type) {
        case 'PHOTOS_HAS_ERROR':
            return action.photosHasError;
        default:
            return state;
    }
}

export function photosIsLoading(state = false, action) {
    actionlog(action, state);
    switch (action.type) {
        case 'PHOTOS_IS_LOADING':
            state.photosIsLoading=action.photosIsLoading;
            return state;
        default:
            return state;
    }
}

export function photos(state = [], action) {
    actionlog(action, state);
    switch (action.type) {
        case 'PHOTOS_FETCH_DATA_SUCCESS':
            return state.concat(action.photos);
        default:
            return state;
    }
}

export const INITIAL_STATE = {
    photos:[],
    photosFound:0,
    photosHasError:false,
    photosIsLoading:false
}

export function photosClear(state = INITIAL_STATE, action) {
    actionlog(action, state);
    switch (action.type) {
        case 'PHOTOS_CLEAR':
            return INITIAL_STATE;
        default:
            return state;
    }
}

export function photosFound(state = [], action) {
    actionlog(action, state);
    switch (action.type) {
        case 'PHOTOS_FOUND':
            return action.photosFound;
        default:
            return state;
    }
}
