import React from "react";

export default React.createContext(null);
/* global document, window */

export default class VKApi {
  constructor(apiId, options = {}) {
    this.apiId = apiId;
    this.options = options;

    this.promise = null;

    this.load();
  }

  init() {
    const {
      apiId,
      options: { onlyWidgets }
    } = this;
    if (apiId) {
      window.VK.init({
        apiId,
        onlyWidgets
      });
    }
  }

  load() {
    const {
      options: { version }
    } = this;

    if (this.promise) return this.promise;

    this.promise = new Promise(resolve => {
      if (window.VK && window.VK.init) {
        this.init();
        resolve(window.VK);
      } else {
        window.vkAsyncInit = () => {
          this.init();
          resolve(window.VK);
        };
      }

      const baseUrl = `https://vk.com/js/api/openapi.js?${version}`;

      if (!document.getElementById("vk-openapi")) {
        const script = document.createElement("script");

        script.type = "text/javascript";
        script.id = "vk-openapi";
        script.src = baseUrl;
        script.async = true;

        document.head.appendChild(script);
      }
    });

    return this.promise;
  }
}
/* global window */
/* eslint-disable import/prefer-default-export */
export const isDOMReady =
  typeof window !== "undefined" &&
  window.document &&
  window.document.createElement;
/* eslint-disable no-underscore-dangle */
import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";

import VKContext from "./VKContext";
import VKApi from "./API";

import { isDOMReady } from "./utils";

const VK = ({ onApiAvailable, apiId, options, children }) => {
  const [vk, setVK] = useState(null);

  useEffect(() => {
    if (isDOMReady) {
      new VKApi(apiId, options).load().then(api => {
        onApiAvailable(api);
        setVK(api);
      });
    }
  }, []);

  return vk ? (
    <VKContext.Provider value={vk}>{children}</VKContext.Provider>
  ) : null;
};

VK.propTypes = {
  apiId: PropTypes.number,
  options: PropTypes.shape({
    version: PropTypes.oneOfType([PropTypes.number, PropTypes.string]),
    onlyWidgets: PropTypes.bool
  }),
  onApiAvailable: PropTypes.func
};

VK.defaultProps = {
  apiId: null,
  options: {
    version: 160,
    onlyWidgets: true
  },
  onApiAvailable: () => {}
};

export default VK;
import React, { useEffect, useContext } from "react";
import PropTypes from "prop-types";
import VKContext from "./VKContext";

const AllowMessagesFromCommunity = ({
  elementId,
  options,
  groupId,
  onAllow,
  onDeny
}) => {
  const vk = useContext(VKContext);

  useEffect(() => {
    vk.Widgets.AllowMessagesFromCommunity(elementId, options, groupId);
    vk.Observer.subscribe(
      "widgets.allowMessagesFromCommunity.allowed",
      userId => onAllow(userId)
    );
    vk.Observer.subscribe("widgets.allowMessagesFromCommunity.denied", userId =>
      onDeny(userId)
    );

    return () =>
      vk.Observer.unsubscribe("widgets.allowMessagesFromCommunity.allowed");
  }, []);

  return <div id={elementId} />;
};

AllowMessagesFromCommunity.propTypes = {
  elementId: PropTypes.string,
  options: PropTypes.shape({
    height: PropTypes.number
  }),
  groupId: PropTypes.number.isRequired,
  onAllow: PropTypes.func,
  onDeny: PropTypes.func
};

AllowMessagesFromCommunity.defaultProps = {
  elementId: "vk_allow_messages_from_community",
  options: {
    height: 24
  },
  onAllow: () => {},
  onDeny: () => {}
};

export default AllowMessagesFromCommunity;
import React, { useContext, useEffect } from "react";
import PropTypes from "prop-types";
import VKContext from "./VKContext";

const Application = ({ elementId, appId, options }) => {
  const vk = useContext(VKContext);

  useEffect(() => {
    vk.Widgets.App(elementId, appId, options);
  }, []);

  return <div id={elementId} />;
};

Application.propTypes = {
  elementId: PropTypes.string,
  appId: PropTypes.number.isRequired,
  options: PropTypes.shape({
    mode: PropTypes.oneOf([1, 2, 3]),
    height: PropTypes.number
  })
};

Application.defaultProps = {
  elementId: "vk_app",
  options: {
    mode: 1
  }
};

export default Application;
import React, { useEffect, useContext } from "react";
import PropTypes from "prop-types";
import VKContext from "./VKContext";

const Auth = ({ elementId, options }) => {
  const vk = useContext(VKContext);

  useEffect(() => {
    const copy = { ...options };
    copy.onAuth = (uid, first_name, last_name, photo, photo_rec, hash) =>
      options.onAuth(uid, first_name, last_name, photo, photo_rec, hash);
    vk.Widgets.Auth(elementId, copy);
  }, []);

  return <div id={elementId} />;
};

Auth.propTypes = {
  elementId: PropTypes.string,
  options: PropTypes.shape({
    width: PropTypes.oneOfType([PropTypes.string, PropTypes.number]),
    authUrl: PropTypes.string,
    onAuth: PropTypes.func
  })
};

Auth.defaultProps = {
  elementId: "vk_auth",
  options: {
    width: 300,
    onAuth: () => {}
  }
};

export default Auth;
import React, { useContext, useEffect } from "react";
import PropTypes from "prop-types";
import VKContext from "./VKContext";

const Comments = ({
  elementId,
  options,
  pageId,
  onNewComment,
  onDeleteComment
}) => {
  const vk = useContext(VKContext);

  useEffect(() => {
    vk.Widgets.Comments(elementId, options, pageId);
    vk.Observer.subscribe(
      "widgets.comments.new_comment",
      (num, last_comment, date, sign) =>
        onNewComment(num, last_comment, date, sign)
    );
    vk.Observer.subscribe("widgets.comments.delete_comment", onDeleteComment);

    return () => {
      vk.Observer.unsubscribe("widgets.comments.new_comment");
      vk.Observer.unsubscribe("widgets.comments.delete_comment");
    };
  }, []);

  return <div id={elementId} />;
};

Comments.propTypes = {
  elementId: PropTypes.string,
  options: PropTypes.shape({
    width: PropTypes.oneOfType([PropTypes.string, PropTypes.number]),
    height: PropTypes.oneOfType([PropTypes.string, PropTypes.number]),
    limit: PropTypes.number,
    attach: PropTypes.oneOf([
      false,
      "graffiti",
      "photo",
      "audio",
      "video",
      "link",
      "*"
    ]),
    autoPublish: PropTypes.oneOf([0, 1]),
    mini: PropTypes.oneOf([0, 1, "auto"]),
    norealtime: PropTypes.oneOf([0, 1]),
    pageUrl: PropTypes.string
  }),
  pageId: PropTypes.string,
  onNewComment: PropTypes.func,
  onDeleteComment: PropTypes.func
};

Comments.defaultProps = {
  elementId: "vk_comments",
  options: {
    height: 0,
    limit: 10,
    attach: "*",
    autoPublish: 0,
    mini: "auto",
    norealtime: 0
  },
  pageId: null,
  onNewComment: () => {},
  onDeleteComment: () => {}
};

export default Comments;
import React, { useEffect, useContext } from "react";
import PropTypes from "prop-types";
import VKContext from "./VKContext";

const CommentsBrowse = ({ elementId, options }) => {
  const vk = useContext(VKContext);

  useEffect(() => {
    vk.Widgets.CommentsBrowse(elementId, options);
  }, []);

  return <div id={elementId} />;
};

CommentsBrowse.propTypes = {
  elementId: PropTypes.string,
  options: PropTypes.shape({
    width: PropTypes.oneOfType([PropTypes.string, PropTypes.number]),
    height: PropTypes.oneOfType([PropTypes.string, PropTypes.number]),
    limit: PropTypes.number,
    mini: PropTypes.oneOf([0, 1, "auto"]),
    norealtime: PropTypes.oneOf([0, 1])
  })
};

CommentsBrowse.defaultProps = {
  elementId: "vk_comments_browse",
  options: {
    height: 0,
    limit: 10,
    mini: "auto",
    norealtime: 0
  }
};

export default CommentsBrowse;
import React, { useContext, useEffect } from "react";
import PropTypes from "prop-types";
import VKContext from "./VKContext";

const CommunityMessages = ({ elementId, groupId, options, onMount }) => {
  const vk = useContext(VKContext);

  useEffect(() => {
    const widget = vk.Widgets.CommunityMessages(elementId, groupId, options);
    onMount(widget, elementId);
  }, []);

  return <div id={elementId} />;
};

CommunityMessages.propTypes = {
  elementId: PropTypes.string,
  groupId: PropTypes.number.isRequired,
  options: PropTypes.shape({
    onCanNotWrite: PropTypes.func,
    welcomeScreen: PropTypes.oneOf([0, 1]),
    expandTimeout: PropTypes.number,
    expanded: PropTypes.number,
